import React, { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, Button, Form } from "react-bootstrap";

const UserPage = () => {
  const [posts, setPosts] = useState([]);
  const [commentInput, setCommentInput] = useState("");
  const [comments, setComments] = useState({});

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/posts")
      .then((response) => {
        setPosts(response.data);
        // Initialize comments state with empty arrays for each post
        const initialComments = {};
        response.data.forEach((post) => {
          initialComments[post.id] = [];
        });
        setComments(initialComments);
      })
      .catch((error) => {
        console.error("There was an error fetching the posts!", error);
      });
  }, []);

  const handleLike = (postId) => {
    axios
      .put(`http://localhost:8080/api/posts/${postId}/like`)
      .then((response) => {
        // Update the state to reflect the new like count
        setPosts(
          posts.map((post) =>
            post.id === postId
              ? { ...post, likeCount: post.likeCount + 1 }
              : post
          )
        );
      })
      .catch((error) => {
        console.error("There was an error liking the post!", error);
      });
  };

  const handleCommentSubmit = (postId) => {
    axios
      .post(`http://localhost:8080/api/comments/create`, {
        postId: postId,
        content: commentInput,
      })
      .then((response) => {
        // Add the new comment to the comments state
        setComments({
          ...comments,
          [postId]: [...comments[postId], response.data],
        });
        // Clear the comment input field
        setCommentInput("");
      })
      .catch((error) => {
        console.error("There was an error adding the comment!", error);
      });
  };

  const handleChangeCommentInput = (event) => {
    setCommentInput(event.target.value);
  };

  const fetchPostsByComment = (commentId) => {
    axios
      .get(`http://localhost:8080/api/comments/${commentId}`)
      .then((response) => {
        // Find the post associated with the comment
        const postWithComment = posts.find(
          (post) => post.id === response.data.postId
        );
        if (postWithComment) {
          // Navigate or display the postWithComment as needed
          console.log("Post with comment:", postWithComment);
        } else {
          console.log("No post found with comment ID:", commentId);
        }
      })
      .catch((error) => {
        console.error(
          "There was an error fetching the post by comment ID!",
          error
        );
      });
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        {posts.map((post) => (
          <Col key={post.id} md={8} className="mb-4">
            <Card>
              <Card.Img variant="top" src={post.img} />
              <Card.Body>
                <Card.Title>{post.name}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">
                  Posted by {post.postedBy} on{" "}
                  {new Date(post.date).toLocaleDateString()}
                </Card.Subtitle>
                <Card.Text>{post.content}</Card.Text>
              </Card.Body>
              <Card.Footer>
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <div>
                    <span className="mr-3">Likes: {post.likeCount}</span>
                    <span>Views: {post.viewCount}</span>
                  </div>
                  <Button variant="primary" href={`/posts/${post.id}`}>
                    Read more
                  </Button>
                </div>
                <div>
                  {post.tags.map((tag) => (
                    <Button
                      key={tag}
                      variant="secondary"
                      size="sm"
                      className="mr-2"
                    >
                      {tag}
                    </Button>
                  ))}
                  <Button
                    variant="outline-primary"
                    size="sm"
                    className="float-right"
                    onClick={() => handleLike(post.id)}
                  >
                    Like
                  </Button>
                </div>
              </Card.Footer>
              <Card.Footer>
                <Form
                  onSubmit={(event) => {
                    event.preventDefault();
                    handleCommentSubmit(post.id);
                  }}
                >
                  <Form.Group controlId={`commentInput-${post.id}`}>
                    <Form.Control
                      type="text"
                      placeholder="Enter your comment"
                      value={commentInput}
                      onChange={handleChangeCommentInput}
                    />
                  </Form.Group>
                  <Button variant="primary" type="submit">
                    Add Comment
                  </Button>
                </Form>
                <div>
                  {comments[post.id].map((comment) => (
                    <p key={comment.id}>{comment.content}</p>
                  ))}
                </div>
              </Card.Footer>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default UserPage;
