import React, { useState, useEffect } from "react";
import axios from "axios";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import { useHistory } from "react-router-dom";

const AdminPage = () => {
  const [userCount, setUserCount] = useState(0);
  const [postCount, setPostCount] = useState(0);
  const [likeCount, setLikeCount] = useState(0);
  const [commentCount, setCommentCount] = useState(0);

  const history = useHistory();

  useEffect(() => {
    // Fetch counts for users, posts, likes, comments
    axios
      .get("http://localhost:8080/api/admin/stats")
      .then((response) => {
        setUserCount(response.data.userCount);
        setPostCount(response.data.postCount);
        setLikeCount(response.data.likeCount);
        setCommentCount(response.data.commentCount);
      })
      .catch((error) => {
        console.error("Error fetching admin stats:", error);
      });
  }, []);

  const handleUserCrud = () => {
    history.push("/admin/users");
  };

  const handlePostCrud = () => {
    history.push("/admin/posts");
  };

  const handleLikeCrud = () => {
    history.push("/admin/likes");
  };

  const handleCommentCrud = () => {
    history.push("/admin/comments");
  };

  return (
    <div className="admin-page">
      <h2>Admin Dashboard</h2>
      <div className="card-container">
        <Card style={{ width: "18rem" }}>
          <Card.Body>
            <Card.Title>Users</Card.Title>
            <Card.Text>Total: {userCount}</Card.Text>
            <Button variant="primary" onClick={handleUserCrud}>
              Manage Users
            </Button>
          </Card.Body>
        </Card>

        <Card style={{ width: "18rem" }}>
          <Card.Body>
            <Card.Title>Posts</Card.Title>
            <Card.Text>Total: {postCount}</Card.Text>
            <Button variant="primary" onClick={handlePostCrud}>
              Manage Posts
            </Button>
          </Card.Body>
        </Card>

        <Card style={{ width: "18rem" }}>
          <Card.Body>
            <Card.Title>Likes</Card.Title>
            <Card.Text>Total: {likeCount}</Card.Text>
            <Button variant="primary" onClick={handleLikeCrud}>
              Manage Likes
            </Button>
          </Card.Body>
        </Card>

        <Card style={{ width: "18rem" }}>
          <Card.Body>
            <Card.Title>Comments</Card.Title>
            <Card.Text>Total: {commentCount}</Card.Text>
            <Button variant="primary" onClick={handleCommentCrud}>
              Manage Comments
            </Button>
          </Card.Body>
        </Card>
      </div>
    </div>
  );
};

export default AdminPage;
