import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Image,
  Modal,
} from "react-bootstrap";
import { FaThumbsUp, FaComment, FaShare } from "react-icons/fa";
import { UserContext } from "./UserContext";

const UserPage = () => {
  const { loggedInUserId } = useContext(UserContext);
  const [posts, setPosts] = useState([]);
  const [comments, setComments] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [modalPostId, setModalPostId] = useState(null);
  const [commentContent, setCommentContent] = useState(""); // State lưu nội dung comment
  const [name, setName] = useState("");
  const [content, setContent] = useState("");
  const [img, setImg] = useState("");

  useEffect(() => {
    // Fetch posts and comments on component mount
    fetchPosts();
  }, []);

  const fetchPosts = () => {
    axios
      .get("http://localhost:8080/api/posts")
      .then((response) => {
        setPosts(response.data);
        const initialComments = {};
        response.data.forEach((post) => {
          axios
            .get(`http://localhost:8080/api/comments/${post.id}`)
            .then((commentResponse) => {
              initialComments[post.id] = commentResponse.data;
              setComments(initialComments);
            })
            .catch((error) => {
              console.error(
                `Error fetching comments for post ${post.id}`,
                error
              );
            });
        });
      })
      .catch((error) => {
        console.error("Error fetching posts:", error);
      });
  };

  const handleLike = (postId) => {
    axios
      .put(`http://localhost:8080/api/posts/${postId}/like`)
      .then((response) => {
        setPosts((prevPosts) =>
          prevPosts.map((post) =>
            post.id === postId
              ? { ...post, likeCount: post.likeCount + 1 }
              : post
          )
        );
      })
      .catch((error) => {
        console.error("Error liking the post:", error);
      });
  };

  const updateComments = (postId, newComment) => {
    setComments((prevComments) => ({
      ...prevComments,
      [postId]: [...prevComments[postId], newComment],
    }));
  };

  const handleShowModal = (postId) => {
    setModalPostId(postId);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleAddPost = () => {
    const postData = {
      name,
      content,
      img,
      user_id: loggedInUserId,
    };

    axios
      .post("http://localhost:8080/api/posts/create", postData)
      .then((response) => {
        setPosts([...posts, response.data]); // Add new post to local state
        setName(""); // Clear form fields after successful submission
        setContent("");
        setImg("");
      })
      .catch((error) => {
        console.error("Error adding post:", error);
      });
  };

  const handleCommentSubmit = (event) => {
    event.preventDefault();
    const commentData = {
      content: commentContent,
      postedBy: { id: loggedInUserId },
      post: { id: modalPostId },
    };

    axios
      .post("http://localhost:8080/api/comments/create", commentData)
      .then((response) => {
        updateComments(modalPostId, response.data); // Update comments after successfully adding new comment
        setCommentContent(""); // Clear input content after successful submission
        handleCloseModal(); // Close modal after successful submission
      })
      .catch((error) => {
        console.error("Error adding comment:", error);
      });
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center mb-4">
        <Col md={8} className="text-center sticky-top bg-white py-3">
          <Button variant="primary" onClick={() => setShowModal(true)}>
            Add Post
          </Button>
        </Col>
      </Row>
      <Row className="justify-content-center">
        {posts.map((post) => (
          <Col key={post.id} md={8} className="mb-4">
            <Card className="shadow-sm">
              <Card.Header className="d-flex align-items-center bg-white">
                <Image
                  src={post.img}
                  roundedCircle
                  style={{ width: "40px", height: "40px", marginRight: "10px" }}
                />
                <div>
                  <Card.Title className="mb-0">{post.name}</Card.Title>
                  <Card.Subtitle className="text-muted">
                    Posted by {post.postedBy.username} on{" "}
                    {new Date(post.date).toLocaleDateString()}
                  </Card.Subtitle>
                </div>
              </Card.Header>
              <Card.Body>
                <Card.Text>{post.content}</Card.Text>
                {post.img && <Card.Img variant="top" src={post.img} />}
              </Card.Body>
              <Card.Footer className="bg-white">
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <div>
                    <Button
                      variant="link"
                      className="text-dark"
                      onClick={() => handleLike(post.id)}
                    >
                      <FaThumbsUp /> {post.likeCount} Likes
                    </Button>
                    <Button
                      variant="link"
                      className="text-dark ml-3"
                      onClick={() => handleShowModal(post.id)}
                    >
                      <FaComment /> Comments
                    </Button>
                    <Button variant="link" className="text-dark ml-3">
                      <FaShare /> Share
                    </Button>
                  </div>
                </div>
                <div className="mt-3">
                  {comments[post.id] &&
                    comments[post.id].map((comment) => (
                      <p key={comment.id} className="mb-1">
                        <strong>{comment.postedBy.username}</strong>:{" "}
                        {comment.content}
                      </p>
                    ))}
                </div>
              </Card.Footer>
            </Card>
          </Col>
        ))}
      </Row>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Add Post</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={handleAddPost}>
            <div className="form-group">
              <label htmlFor="postName">Name</label>
              <input
                type="text"
                className="form-control"
                id="postName"
                placeholder="Enter post name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="postContent">Content</label>
              <textarea
                className="form-control"
                id="postContent"
                rows="3"
                placeholder="Enter post content"
                value={content}
                onChange={(e) => setContent(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="postImg">Image URL (optional)</label>
              <input
                type="text"
                className="form-control"
                id="postImg"
                placeholder="Enter image URL"
                value={img}
                onChange={(e) => setImg(e.target.value)}
              />
            </div>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </form>
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default UserPage;
