import React, { useState, useEffect } from "react";
import axios from "axios";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Table from "react-bootstrap/Table";
import Container from "react-bootstrap/Container";

const AdminPage = () => {
  const [userCount, setUserCount] = useState(0);
  const [postCount, setPostCount] = useState(0);
  const [likeCount, setLikeCount] = useState(0);
  const [commentCount, setCommentCount] = useState(0);
  const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);

  const fetchUsers = () => {
    axios
      .get("http://localhost:8080/api/auth/users")
      .then((response) => {
        setUsers(response.data);

        // Update userCount state with the number of users fetched
        setUserCount(response.data.length);
      })
      .catch((error) => {
        console.error("Error fetching users:", error);
      });
  };

  return (
    <Container className="mt-4">
      <h2 className="mb-4">Admin Dashboard</h2>

      <div className="card-container d-flex justify-content-around flex-wrap">
        <Card style={{ width: "18rem" }} className="mb-4" onClick={fetchUsers}>
          <Card.Body>
            <Card.Title>Users</Card.Title>
            <Card.Text>Total: {userCount}</Card.Text>
            <Button variant="primary">Manage Users</Button>
          </Card.Body>
        </Card>

        {/* Similar cards for Posts, Likes, and Comments */}
        {/* Update onClick handlers for respective functionalities */}

        {/* Posts Card */}
        <Card style={{ width: "18rem" }} className="mb-4">
          <Card.Body>
            <Card.Title>Posts</Card.Title>
            <Card.Text>Total: {postCount}</Card.Text>
            <Button
              variant="primary"
              onClick={() => console.log("Manage Posts")}
            >
              Manage Posts
            </Button>
          </Card.Body>
        </Card>

        {/* Likes Card */}
        <Card style={{ width: "18rem" }} className="mb-4">
          <Card.Body>
            <Card.Title>Likes</Card.Title>
            <Card.Text>Total: {likeCount}</Card.Text>
            <Button
              variant="primary"
              onClick={() => console.log("Manage Likes")}
            >
              Manage Likes
            </Button>
          </Card.Body>
        </Card>

        {/* Comments Card */}
        <Card style={{ width: "18rem" }} className="mb-4">
          <Card.Body>
            <Card.Title>Comments</Card.Title>
            <Card.Text>Total: {commentCount}</Card.Text>
            <Button
              variant="primary"
              onClick={() => console.log("Manage Comments")}
            >
              Manage Comments
            </Button>
          </Card.Body>
        </Card>
      </div>

      {/* Display Users Table */}
      {users.length > 0 && (
        <div>
          <h3 className="mb-3">Users Table</h3>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>#</th>
                <th>Username</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user, index) => (
                <tr key={user.id}>
                  <td>{index + 1}</td>
                  <td>{user.username}</td>
                  <td>
                    <Button
                      variant="info"
                      className="mr-2"
                      onClick={() => console.log("Edit User")}
                    >
                      Edit
                    </Button>
                    <Button
                      variant="danger"
                      onClick={() => console.log("Delete User")}
                    >
                      Delete
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      )}

      {/* Similar sections for Posts, Likes, and Comments tables */}
      {/* Update these sections with data fetching and rendering logic */}
    </Container>
  );
};

export default AdminPage;
