import axios from "axios";
import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card, Image, Button } from "react-bootstrap";
import { FaThumbsUp, FaComment, FaShare } from "react-icons/fa";
import { useFetcher, useParams } from "react-router-dom";

const PostDetail = ({}) => {
  const id = useParams();

  const [views, setViews] = useState();
  const [postDetail, setPostDetail] = useState();

  useEffect((id) => {
    axios
      .get(`http://localhost:8080/api/posts/${id}`)
      .then((response) => {
        setPostDetail(response.data);
      })
      .catch((error) => {
        console.log("Error get post by id");
      });
  }, []);

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col key={postDetail.id} md={8} className="mb-4">
          <Card className="shadow-sm">
            <Card.Header className="d-flex align-items-center bg-white">
              <Image
                src={postDetail.img}
                roundedCircle
                style={{ width: "40px", height: "40px", marginRight: "10px" }}
              />
              <div>
                <Card.Title className="mb-0">{postDetail.name}</Card.Title>
                <Card.Subtitle className="text-muted">
                  Được đăng bởi {postDetail.postedBy.username} vào{" "}
                  {new Date(postDetail.date).toLocaleDateString()}
                </Card.Subtitle>
              </div>
            </Card.Header>
            <Card.Body>
              {post.img && <Card.Img variant="top" src={postDetail.img} />}
              {/* <button className="btn btn-primary mt-2">Xem bài viết</button> */}
            </Card.Body>
            <Card.Footer className="bg-white">
              <div className="d-flex justify-content-around">
                {/* <Button
                    variant="link"
                    className="text-primary"
                    onClick={() =>
                      likedPosts.includes(post.id)
                        ? handleUnlike(post.id)
                        : handleLike(post.id)
                    }
                  >
                    {/* <FaThumbsUp /> {post.likeCount} Thích
                  </Button>
                  <Button
                    variant="link"
                    className="text-primary"
                    onClick={() => handleShowModal(post.id)}
                  >
                    <FaComment /> Bình luận
                  </Button>
                  <Button variant="link" className="text-primary">
                    <FaShare /> Chia sẻ
                  </Button> */}
              </div>
              {/* {comments[post.id] &&
                comments[post.id].map((comment) => (
                  <div key={comment.id} className="mt-2">
                    <strong>{comment.postedBy.username}: </strong>
                    {comment.content}
                  </div>
                ))} */}
            </Card.Footer>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default PostDetail;
