import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Form,
  Image,
  Modal,
} from "react-bootstrap";
import { FaThumbsUp, FaComment, FaShare } from "react-icons/fa";
import { UserContext } from "../UserContext";

const UserPage = () => {
  const { loggedInUserId } = useContext(UserContext); // Nhận loggedInUserId từ context
  const [posts, setPosts] = useState([]);
  const [commentInput, setCommentInput] = useState({}); // State lưu nội dung comment
  const [comments, setComments] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [name, setName] = useState("");
  const [content, setContent] = useState("");
  const [img, setImg] = useState("");
  const [tags, setTags] = useState("");

  useEffect(() => {
    // Lấy danh sách bài viết từ server khi component mount
    axios
      .get("http://localhost:8080/api/posts")
      .then((response) => {
        setPosts(response.data);
        const initialComments = {};
        // Khởi tạo comments cho mỗi bài viết
        response.data.forEach((post) => {
          initialComments[post.id] = [];
          // Lấy danh sách comment cho mỗi bài viết
          axios
            .get(`http://localhost:8080/api/comments/${post.id}`)
            .then((commentResponse) => {
              setComments((prevComments) => ({
                ...prevComments,
                [post.id]: commentResponse.data,
              }));
            })
            .catch((error) => {
              console.error(
                `Error fetching comments for post ${post.id}`,
                error
              );
            });
        });
        setComments(initialComments);
      })
      .catch((error) => {
        console.error("There was an error fetching the posts!", error);
      });
  }, []);

  const handleLike = (postId) => {
    // Xử lý khi người dùng like một bài viết
    axios
      .put(`http://localhost:8080/api/posts/${postId}/like`)
      .then((response) => {
        setPosts(
          posts.map((post) =>
            post.id === postId
              ? { ...post, likeCount: post.likeCount + 1 }
              : post
          )
        );
      })
      .catch((error) => {
        console.error("There was an error liking the post!", error);
      });
  };

  const handleCommentSubmit = (postId) => {
    // Xử lý khi người dùng thêm comment vào một bài viết
    const commentData = {
      content: commentInput[postId],
      postedBy: { id: loggedInUserId }, // Sử dụng ID của người dùng đã đăng nhập
      post: { id: postId }, // Sử dụng ID của bài viết mà bạn đang comment
    };

    axios
      .post(`http://localhost:8080/api/comments/create`, commentData)
      .then((response) => {
        setComments({
          ...comments,
          [postId]: [...comments[postId], response.data],
        });
        setCommentInput({ ...commentInput, [postId]: "" }); // Xóa nội dung comment sau khi gửi thành công
      })
      .catch((error) => {
        console.error("There was an error adding the comment!", error);
      });
  };

  const handleChangeCommentInput = (postId, event) => {
    // Cập nhật nội dung comment vào state khi người dùng nhập
    setCommentInput({ ...commentInput, [postId]: event.target.value });
  };

  const handleAddPost = () => {
    // Hiển thị modal để người dùng thêm bài viết mới
    setName("");
    setContent("");
    setImg("");
    setTags("");
    setShowModal(true);
  };

  const handleCloseModal = () => {
    // Đóng modal thêm bài viết mới
    setShowModal(false);
  };

  const handleSubmitPost = () => {
    // Xử lý khi người dùng thêm bài viết mới
    const postData = {
      name,
      content,
      img,
      tags: tags.split(",").map((tag) => tag.trim()),
      postedBy: { id: loggedInUserId }, // Sử dụng ID của người dùng đã đăng nhập
    };

    axios
      .post("http://localhost:8080/api/posts", postData)
      .then((response) => {
        setPosts([...posts, response.data]);
        setShowModal(false); // Đóng modal sau khi thêm bài viết thành công
      })
      .catch((error) => {
        console.error("Error adding the post:", error);
      });
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center mb-4">
        <Col md={8} className="text-center sticky-top bg-white py-3">
          <Button variant="primary" onClick={handleAddPost}>
            Add Post
          </Button>
        </Col>
      </Row>
      <Row className="justify-content-center">
        {posts.map((post) => (
          <Col key={post.id} md={8} className="mb-4">
            <Card className="shadow-sm">
              <Card.Header className="d-flex align-items-center bg-white">
                <Image
                  src={post.img}
                  roundedCircle
                  style={{ width: "40px", height: "40px", marginRight: "10px" }}
                />
                <div>
                  <Card.Title className="mb-0">{post.name}</Card.Title>
                  <Card.Subtitle className="text-muted">
                    Posted by {post.postedBy.username} on{" "}
                    {new Date(post.date).toLocaleDateString()}
                  </Card.Subtitle>
                </div>
              </Card.Header>
              <Card.Body>
                <Card.Text>{post.content}</Card.Text>
                {post.img && <Card.Img variant="top" src={post.img} />}
              </Card.Body>
              <Card.Footer className="bg-white">
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <div>
                    <Button
                      variant="link"
                      className="text-dark"
                      onClick={() => handleLike(post.id)}
                      style={{ textDecoration: "none" }}
                    >
                      <FaThumbsUp /> {post.likeCount} Likes
                    </Button>
                    <Button
                      variant="link"
                      className="text-dark ml-3"
                      style={{ textDecoration: "none" }}
                    >
                      <FaComment /> Comments
                    </Button>
                    <Button
                      variant="link"
                      className="text-dark ml-3"
                      style={{ textDecoration: "none" }}
                    >
                      <FaShare /> Share
                    </Button>
                  </div>
                </div>
                <div>
                  <Form
                    onSubmit={(event) => {
                      event.preventDefault();
                      handleCommentSubmit(post.id);
                    }}
                  >
                    <Form.Control
                      type="text"
                      placeholder="Write a comment..."
                      value={commentInput[post.id] || ""}
                      onChange={(event) =>
                        handleChangeCommentInput(post.id, event)
                      }
                      style={{
                        borderRadius: "20px",
                        paddingLeft: "15px",
                      }}
                    />
                    <Button
                      variant="primary"
                      type="submit"
                      className="mt-2"
                      style={{ borderRadius: "20px" }}
                    >
                      Post
                    </Button>
                  </Form>
                  <div className="mt-3">
                    {comments[post.id] &&
                      comments[post.id].map((comment) => (
                        <p key={comment.id} className="mb-1">
                          <strong>{comment.postedBy.username}</strong>:{" "}
                          {comment.content}
                        </p>
                      ))}
                  </div>
                </div>
              </Card.Footer>
            </Card>
          </Col>
        ))}
      </Row>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Add Post</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="postName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="postContent">
              <Form.Label>Content</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Enter content"
                value={content}
                onChange={(e) => setContent(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="postImage">
              <Form.Label>Image URL</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter image URL"
                value={img}
                onChange={(e) => setImg(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="postTags">
              <Form.Label>Tags (comma-separated)</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tags"
                value={tags}
                onChange={(e) => setTags(e.target.value)}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSubmitPost}>
            Add Post
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default UserPage;
