import React, { useState, useEffect } from "react";
import axios from "axios";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Table from "react-bootstrap/Table";
import Container from "react-bootstrap/Container";

const AdminPage = () => {
  const [userCount, setUserCount] = useState(0);
  const [postCount, setPostCount] = useState(0);
  const [likeCount, setLikeCount] = useState(0);
  const [commentCount, setCommentCount] = useState(0);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    // Fetch counts for users, posts, likes, comments
    axios
      .get("http://localhost:8080/api/admin/stats")
      .then((response) => {
        setUserCount(response.data.userCount);
        setPostCount(response.data.postCount);
        setLikeCount(response.data.likeCount);
        setCommentCount(response.data.commentCount);
      })
      .catch((error) => {
        console.error("Error fetching admin stats:", error);
      });

    // Fetch all posts for detailed table view
    axios
      .get("http://localhost:8080/api/posts")
      .then((response) => {
        setPosts(response.data);
      })
      .catch((error) => {
        console.error("Error fetching posts:", error);
      });
  }, []);

  return (
    <Container className="mt-4">
      <h2 className="mb-4">Admin Dashboard</h2>

      <div className="card-container d-flex justify-content-around flex-wrap">
        <Card style={{ width: "18rem" }} className="mb-4">
          <Card.Body>
            <Card.Title>Users</Card.Title>
            <Card.Text>Total: {userCount}</Card.Text>
            <Button
              variant="primary"
              onClick={() => console.log("Manage Users")}
            >
              Manage Users
            </Button>
          </Card.Body>
        </Card>

        <Card style={{ width: "18rem" }} className="mb-4">
          <Card.Body>
            <Card.Title>Posts</Card.Title>
            <Card.Text>Total: {postCount}</Card.Text>
            <Button
              variant="primary"
              onClick={() => console.log("Manage Posts")}
            >
              Manage Posts
            </Button>
          </Card.Body>
        </Card>

        <Card style={{ width: "18rem" }} className="mb-4">
          <Card.Body>
            <Card.Title>Likes</Card.Title>
            <Card.Text>Total: {likeCount}</Card.Text>
            <Button
              variant="primary"
              onClick={() => console.log("Manage Likes")}
            >
              Manage Likes
            </Button>
          </Card.Body>
        </Card>

        <Card style={{ width: "18rem" }} className="mb-4">
          <Card.Body>
            <Card.Title>Comments</Card.Title>
            <Card.Text>Total: {commentCount}</Card.Text>
            <Button
              variant="primary"
              onClick={() => console.log("Manage Comments")}
            >
              Manage Comments
            </Button>
          </Card.Body>
        </Card>
      </div>

      <h3 className="mb-3">Posts Table</h3>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Content</th>
            <th>Posted By</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {posts.map((post, index) => (
            <tr key={post.id}>
              <td>{index + 1}</td>
              <td>{post.name}</td>
              <td>{post.content}</td>
              <td>{post.postedBy.username}</td>
              <td>
                <Button
                  variant="info"
                  className="mr-2"
                  onClick={() => console.log("Edit Post")}
                >
                  Edit
                </Button>
                <Button
                  variant="danger"
                  onClick={() => console.log("Delete Post")}
                >
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default AdminPage;
