  Button,
  Image,
  Modal,
  Form,
} from "react-bootstrap";
import { FaThumbsUp, FaComment, FaShare, FaSearch } from "react-icons/fa";
import { UserContext } from "./UserContext";

const UserPage = () => {
  const { loggedInUserId } = useContext(UserContext);
  const [posts, setPosts] = useState([]);
  const [comments, setComments] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [modalPostId, setModalPostId] = useState(null);
  const [commentContent, setCommentContent] = useState("");
  const [name, setName] = useState("");
  const [content, setContent] = useState("");
  const [img, setImg] = useState("");
  const [tags, setTags] = useState("");
  const [postedBy, setPostedBy] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [likedPosts, setLikedPosts] = useState([]);
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [userDetails, setUserDetails] = useState({
    username: "",
    password: "",
    id: "",
  });

  useEffect(() => {
    fetchPosts();
    fetchUserDetails();
  }, []);

  const fetchPosts = () => {
    axios
      .get("http://localhost:8080/api/posts")
      .then((response) => {
        setPosts(response.data);
        const initialComments = {};
        response.data.forEach((post) => {
          axios
            .get(`http://localhost:8080/api/comments/${post.id}`)
            .then((commentResponse) => {
              initialComments[post.id] = commentResponse.data;
              setComments(initialComments);
            })
            .catch((error) => {
              console.error(
                `Error fetching comments for post ${post.id}`,
                error
              );
            });
        });
      })
      .catch((error) => {
        console.error("Error fetching posts:", error);
      });
  };

  const fetchUserDetails = () => {
    axios
      .get(`http://localhost:8080/api/auth/users/${loggedInUserId}`)
      .then((response) => {
        setUserDetails(response.data);
      })
      .catch((error) => {
        console.error("Error fetching user details:", error);
      });
  };

  const handleProfileUpdate = (id) => {
    event.preventDefault();
    const updatedUser = { ...userDetails, id: loggedInUserId };
    axios
      .put(`http://localhost:8080/api/auth/users/${id}`, updatedUser)
      .then((response) => {
        setUserDetails(response.data);
        setShowProfileModal(false);
      })
      .catch((error) => {
        console.error("Error updating profile:", error);
      });
  };

  const handleLike = (postId) => {
    axios
      .put(`http://localhost:8080/api/posts/${postId}/like`)
      .then((response) => {
        setPosts((prevPosts) =>
          prevPosts.map((post) =>
            post.id === postId
              ? { ...post, likeCount: post.likeCount + 1 }
              : post
          )
        );
        setLikedPosts((prevLikedPosts) => [...prevLikedPosts, postId]);
      })
      .catch((error) => {
        console.error("Error liking the post:", error);
      });
  };

  const handleUnlike = (postId) => {
    axios
      .put(`http://localhost:8080/api/posts/${postId}/Unlike`)
      .then((response) => {
        setPosts((prevPosts) =>
          prevPosts.map((post) =>
            post.id === postId
              ? { ...post, likeCount: post.likeCount - 1 }
              : post
          )
        );
        setLikedPosts((prevLikedPosts) =>
          prevLikedPosts.filter((id) => id !== postId)
        );
      })
      .catch((error) => {
        console.error("Error unliking the post:", error);
      });
  };

  const updateComments = (postId, newComment) => {
    setComments((prevComments) => ({
      ...prevComments,
      [postId]: [...prevComments[postId], newComment],
    }));
  };

  const handleShowModal = (postId) => {
    setModalPostId(postId);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setModalPostId(null);
  };

  const handleAddPost = (event) => {
    event.preventDefault();
    const postData = {
      name,
      content,
      img,
      tags: tags.split(",").map((tag) => tag.trim()),
      postedBy: { id: parseInt(postedBy) },
    };

    axios
      .post("http://localhost:8080/api/posts", postData)
      .then((response) => {
        setPosts([...posts, response.data]);
        setName("");
        setContent("");
        setImg("");
        setTags("");
        setPostedBy("");
        handleCloseModal();
      })
      .catch((error) => {
        console.error("Error adding post:", error);
      });
  };

  const handleCommentSubmit = (event, postId) => {
    event.preventDefault();
    const commentData = {
      content: commentContent,
      post: { id: postId },
    };

    axios
      .post("http://localhost:8080/api/comments/create", commentData)
      .then((response) => {
        updateComments(postId, response.data);
        setCommentContent("");
        handleCloseModal();
      })
      .catch((error) => {
        console.error("Error adding comment:", error);
      });
  };

  const handleSearch = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/posts/search/${searchTerm}`
      );
      setSearchResults(response.data);
    } catch (error) {
      console.error("Error searching posts:", error);
    }
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center mb-4">
        <Col md={8} className="text-center sticky-top bg-white py-3">
          <Button variant="primary" onClick={() => setShowModal(true)}>
            Thêm bài đăng
          </Button>
          <Button
            variant="secondary"
            onClick={() => {
              fetchUserDetails();
              setShowProfileModal(true);
            }}
          >
            Chỉnh sửa hồ sơ
          </Button>
        </Col>
      </Row>
      <Row className="justify-content-center mb-4">
        <Col md={8} className="text-center">
          <Form.Group>
            <Form.Control
              type="text"
              placeholder="Tìm kiếm bài đăng..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </Form.Group>
          <Button variant="primary" onClick={handleSearch}>
            <FaSearch /> Tìm kiếm
          </Button>
        </Col>
      </Row>
      <Row className="justify-content-center">
        {(searchResults.length > 0 ? searchResults : posts).map((post) => (
          <Col key={post.id} md={8} className="mb-4">
            <Card className="shadow-sm">
              <Card.Header className="d-flex align-items-center bg-white">
                <Image
                  src={post.img}
                  roundedCircle
                  style={{ width: "40px", height: "40px", marginRight: "10px" }}
                />
                <div>
                  <Card.Title className="mb-0">{post.name}</Card.Title>
                  <Card.Subtitle className="text-muted">
                    Được đăng bởi {post.postedBy.username} vào{" "}
                    {new Date(post.date).toLocaleDateString()}
                  </Card.Subtitle>
                </div>
              </Card.Header>
              <Card.Body>
                <Card.Text>{post.content}</Card.Text>
                {post.img && <Card.Img variant="top" src={post.img} />}
              </Card.Body>
              <Card.Footer className="bg-white">
                <div className="d-flex justify-content-around">
                  <Button
                    variant="link"
                    className="text-primary"
                    onClick={() =>
                      likedPosts.includes(post.id)
                        ? handleUnlike(post.id)
                        : handleLike(post.id)
                    }
                  >
                    <FaThumbsUp /> {post.likeCount} Thích
                  </Button>
                  <Button
                    variant="link"
                    className="text-primary"
                    onClick={() => handleShowModal(post.id)}
                  >
                    <FaComment /> Bình luận
                  </Button>
                  <Button variant="link" className="text-primary">
                    <FaShare /> Chia sẻ
                  </Button>
                </div>
                {comments[post.id] &&
                  comments[post.id].map((comment) => (
                    <div key={comment.id} className="mt-2">
                      <strong>{comment.postedBy.username}:</strong>{" "}
                      {comment.content}
                    </div>
                  ))}
              </Card.Footer>
            </Card>
          </Col>
        ))}
      </Row>

      {/* Modal Thêm bài đăng */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Thêm bài đăng mới</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleAddPost}>
            <Form.Group controlId="name">
              <Form.Label>Tên bài đăng</Form.Label>
              <Form.Control
                type="text"
                placeholder="Nhập tên bài đăng"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="content">
              <Form.Label>Nội dung bài đăng</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Nhập nội dung bài đăng"
                value={content}
                onChange={(e) => setContent(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="img">
              <Form.Label>Đường dẫn hình ảnh (nếu có)</Form.Label>
              <Form.Control
                type="text"
                placeholder="Nhập đường dẫn hình ảnh"
                value={img}
                onChange={(e) => setImg(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="tags">
              <Form.Label>Tags (phân cách bằng dấu phẩy)</Form.Label>
              <Form.Control
                type="text"
                placeholder="Nhập tags"
                value={tags}
                onChange={(e) => setTags(e.target.value)}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Đăng bài
            </Button>
          </Form>
        </Modal.Body>
      </Modal>

      {/* Modal Bình luận */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Bình luận bài đăng</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={(e) => handleCommentSubmit(e, modalPostId)}>
            <Form.Group controlId="commentContent">
              <Form.Label>Nội dung bình luận</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Nhập nội dung bình luận"
                value={commentContent}
                onChange={(e) => setCommentContent(e.target.value)}
                required
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Bình luận
            </Button>
          </Form>
        </Modal.Body>
      </Modal>

      {/* Modal Chỉnh sửa hồ sơ */}
      <Modal show={showProfileModal} onHide={() => setShowProfileModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Chỉnh sửa hồ sơ</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={() => handleProfileUpdate(userDetails.id)}>
            <Form.Group controlId="username">
              <Form.Label>Tên người dùng</Form.Label>
              <Form.Control
                type="text"
                placeholder="Nhập tên người dùng"
                value={userDetails.username}
                onChange={(e) =>
                  setUserDetails({ ...userDetails, username: e.target.value })
                }
                required
              />
            </Form.Group>
            <Form.Group controlId="password">
              <Form.Label>Mật khẩu</Form.Label>
              <Form.Control
                type="password"
                placeholder="Nhập mật khẩu"
                value={userDetails.password}
                onChange={(e) =>
                  setUserDetails({ ...userDetails, password: e.target.value })
                }
                required
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Lưu thay đổi
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default UserPage;
