import axios from "axios";
import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card, Button, Carousel } from "react-bootstrap";
import { Link } from "react-router-dom";

const Home = () => {
  const [newPost, setNewPost] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/posts/newPost")
      .then((response) => {
        setNewPost(response.data);
      })
      .catch((error) => {
        console.log("Error axios data new post !");
      });
  }, []);
  return (
    <div>
      <Container className="mt-4">
        <Carousel>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src="https://i1.wp.com/theappentrepreneur.com/wp-content/uploads/2013/02/Blogging-Apps-For-Bloggers.jpg?fit=730%2C382&ssl=1"
              alt="First slide"
            />
            <Carousel.Caption>
              <h3></h3>
              <p></p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src="https://www.teknopk.com/wp-content/uploads/2018/03/blogging.jpg"
              alt="Second slide"
            />
            <Carousel.Caption>
              <h3></h3>
              <p></p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src="https://www.hallaminternet.com/wp-content/uploads/2020/01/Is-blogging-relevant-anymore.jpeg"
              alt="Third slide"
            />
            <Carousel.Caption>
              <h3></h3>
              <p></p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>

        <h2 className="text-center my-4">New Post</h2>
        <Row>
          {newPost.map((post, index) => (
            <Col md={4}>
              <Card className="mb-4">
                <Card.Img
                  variant="top"
                  src={post.img || "https://via.placeholder.com/150"}
                />
                <Card.Body>
                  <Card.Title>{post.name}</Card.Title>
                  <Card.Text>{post.content}</Card.Text>
                  {/* <Button variant="primary" href="/article/1">
                  Read more
                </Button> */}
                </Card.Body>
              </Card>
            </Col>
          ))}
          ;
        </Row>
      </Container>
    </div>
  );
};

export default Home;
