import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Form,
  Image,
  Modal,
} from "react-bootstrap";
import { FaThumbsUp, FaComment, FaShare } from "react-icons/fa";
import { Link } from "react-router-dom";

const UserPage = () => {
  const [posts, setPosts] = useState([]);
  const [commentInput, setCommentInput] = useState("");
  const [comments, setComments] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [name, setName] = useState("");
  const [content, setContent] = useState("");
  const [img, setImg] = useState("");
  const [tags, setTags] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/posts")
      .then((response) => {
        setPosts(response.data);
        // Initialize comments state with empty arrays for each post
        const initialComments = {};
        response.data.forEach((post) => {
          initialComments[post.id] = [];
        });
        setComments(initialComments);
        // Fetch comments for each post
        response.data.forEach((post) => {
          axios
            .get(`http://localhost:8080/api/comments/${post.id}`)
            .then((commentResponse) => {
              // Update comments state with comments for this post
              setComments((prevComments) => ({
                ...prevComments,
                [post.id]: commentResponse.data,
              }));
            })
            .catch((error) => {
              console.error(
                `Error fetching comments for post ${post.id}`,
                error
              );
            });
        });
      })
      .catch((error) => {
        console.error("There was an error fetching the posts!", error);
      });
  }, []);

  const handleLike = (postId) => {
    axios
      .put(`http://localhost:8080/api/posts/${postId}/like`)
      .then((response) => {
        // Update the state to reflect the new like count
        setPosts(
          posts.map((post) =>
            post.id === postId
              ? { ...post, likeCount: post.likeCount + 1 }
              : post
          )
        );
      })
      .catch((error) => {
        console.error("There was an error liking the post!", error);
      });
  };

  const handleCommentSubmit = (postId) => {
    axios
      .post(`http://localhost:8080/api/comments/create`, {
        postId: postId,
        content: commentInput,
        posted_by: "User", // Replace with actual user data if available
      })
      .then((response) => {
        // Add the new comment to the comments state
        setComments({
          ...comments,
          [postId]: [...comments[postId], response.data],
        });
        // Clear the comment input field
        setCommentInput("");
      })
      .catch((error) => {
        console.error("There was an error adding the comment!", error);
      });
  };

  const handleChangeCommentInput = (event) => {
    setCommentInput(event.target.value);
  };

  const handleAddPost = () => {
    // Reset form state
    setName("");
    setContent("");
    setImg("");
    setTags("");

    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleSubmitPost = () => {
    axios
      .post("http://localhost:8080/api/posts/create", {
        name,
        content,
        img,
        user_id: userId, // Replace with actual userId from context
        tags,
      })
      .then((response) => {
        // Update posts state with the new post
        setPosts([...posts, response.data]);
        setShowModal(false); // Close the modal after successful submission
      })
      .catch((error) => {
        console.error("Error adding post:", error);
      });
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center mb-4">
        <Col md={8} className="text-center sticky-top bg-white py-3">
          <Button variant="primary" onClick={handleAddPost}>
            Add Post
          </Button>
        </Col>
      </Row>
      <Row className="justify-content-center">
        {posts.map((post) => (
          <Col key={post.id} md={8} className="mb-4">
            <Card>
              <Card.Header className="d-flex align-items-center">
                <Image
                  src={post.img}
                  roundedCircle
                  style={{ width: "40px", height: "40px", marginRight: "10px" }}
                />
                <div>
                  <Card.Title>{post.name}</Card.Title>
                  <Card.Subtitle className="mb-2 text-muted">
                    Posted by {post.postedBy} on{" "}
                    {new Date(post.date).toLocaleDateString()}
                  </Card.Subtitle>
                </div>
              </Card.Header>
              <Card.Body>
                <Card.Text>{post.content}</Card.Text>
                <Card.Img variant="top" src={post.img} />
              </Card.Body>
              <Card.Footer>
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <div>
                    <Button variant="link" onClick={() => handleLike(post.id)}>
                      <FaThumbsUp /> {post.likeCount} Likes
                    </Button>
                    <Button variant="link" className="ml-3">
                      <FaComment /> Comments
                    </Button>
                    <Button variant="link" className="ml-3">
                      <FaShare /> Share
                    </Button>
                  </div>
                </div>
                <div>
                  <Form
                    onSubmit={(event) => {
                      event.preventDefault();
                      handleCommentSubmit(post.id);
                    }}
                  >
                    <Form.Group controlId={`commentInput-${post.id}`}>
                      <Form.Control
                        type="text"
                        placeholder="Write a comment..."
                        value={commentInput}
                        onChange={handleChangeCommentInput}
                      />
                    </Form.Group>
                    <Button variant="primary" type="submit">
                      Post
                    </Button>
                  </Form>
                  <div className="mt-3">
                    {comments[post.id].map((comment) => (
                      <p key={comment.id}>
                        <strong>{comment.posted_by}</strong>: {comment.content}
                      </p>
                    ))}
                  </div>
                </div>
              </Card.Footer>
            </Card>
          </Col>
        ))}
      </Row>

      {/* Add Post Modal */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Add Post</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="postName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="postContent">
              <Form.Label>Content</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Enter content"
                value={content}
                onChange={(e) => setContent(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="postImg">
              <Form.Label>Image URL</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter image URL"
                value={img}
                onChange={(e) => setImg(e.target.value)}
              />
            </Form.Group>
            <Form.Group controlId="postTags">
              <Form.Label>Tags</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter tags (optional)"
                value={tags}
                onChange={(e) => setTags(e.target.value)}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSubmitPost}>
            Add Post
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default UserPage;
