import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Image,
  Modal,
  Form,
} from "react-bootstrap";
import { FaThumbsUp, FaComment, FaShare, FaSearch } from "react-icons/fa";
import { UserContext } from "./UserContext";

const UserPage = () => {
  const { loggedInUserId } = useContext(UserContext);
  const [posts, setPosts] = useState([]);
  const [comments, setComments] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [modalPostId, setModalPostId] = useState(null);
  const [commentContent, setCommentContent] = useState("");
  const [name, setName] = useState("");
  const [content, setContent] = useState("");
  const [img, setImg] = useState("");
  const [tags, setTags] = useState("");
  const [postedBy, setPostedBy] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = () => {
    axios
      .get("http://localhost:8080/api/posts")
      .then((response) => {
        setPosts(response.data);
        const initialComments = {};
        response.data.forEach((post) => {
          axios
            .get(`http://localhost:8080/api/comments/${post.id}`)
            .then((commentResponse) => {
              initialComments[post.id] = commentResponse.data;
              setComments(initialComments);
            })
            .catch((error) => {
              console.error(
                `Error fetching comments for post ${post.id}`,
                error
              );
            });
        });
      })
      .catch((error) => {
        console.error("Error fetching posts:", error);
      });
  };

  const handleLike = (postId) => {
    axios
      .put(`http://localhost:8080/api/posts/${postId}/like`)
      .then((response) => {
        setPosts((prevPosts) =>
          prevPosts.map((post) =>
            post.id === postId
              ? { ...post, likeCount: post.likeCount + 1 }
              : post
          )
        );
      })
      .catch((error) => {
        console.error("Error liking the post:", error);
      });
  };

  const updateComments = (postId, newComment) => {
    setComments((prevComments) => ({
      ...prevComments,
      [postId]: [...prevComments[postId], newComment],
    }));
  };

  const handleShowModal = (postId) => {
    setModalPostId(postId);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setModalPostId(null);
  };

  const handleAddPost = (event) => {
    event.preventDefault();
    const postData = {
      name,
      content,
      img,
      tags: tags.split(",").map((tag) => tag.trim()),
      postedBy: { id: parseInt(postedBy) }, // Assuming `postedBy` is an object with an `id` field
    };

    axios
      .post("http://localhost:8080/api/posts", postData)
      .then((response) => {
        setPosts([...posts, response.data]);
        setName("");
        setContent("");
        setImg("");
        setTags("");
        setPostedBy("");
        handleCloseModal();
      })
      .catch((error) => {
        console.error("Error adding post:", error);
      });
  };

  const handleCommentSubmit = (event, postId) => {
    event.preventDefault();
    const commentData = {
      content: commentContent,
      post: { id: postId },
    };

    axios
      .post("http://localhost:8080/api/comments/create", commentData)
      .then((response) => {
        updateComments(postId, response.data);
        setCommentContent("");
        handleCloseModal();
      })
      .catch((error) => {
        console.error("Error adding comment:", error);
      });
  };

  const handleSearch = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/posts/search/${searchTerm}`
      );
      setSearchResults(response.data);
    } catch (error) {
      console.error("Error searching posts:", error);
    }
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center mb-4">
        <Col md={8} className="text-center sticky-top bg-white py-3">
          <Button variant="primary" onClick={() => setShowModal(true)}>
            Thêm bài đăng
          </Button>
        </Col>
      </Row>
      <Row className="justify-content-center mb-4">
        <Col md={8} className="text-center">
          <Form.Group>
            <Form.Control
              type="text"
              placeholder="Tìm kiếm bài đăng..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </Form.Group>
          <Button variant="primary" onClick={handleSearch}>
            <FaSearch /> Tìm kiếm
          </Button>
        </Col>
      </Row>
      <Row className="justify-content-center">
        {(searchResults.length > 0 ? searchResults : posts).map((post) => (
          <Col key={post.id} md={8} className="mb-4">
            <Card className="shadow-sm">
              <Card.Header className="d-flex align-items-center bg-white">
                <Image
                  src={post.img}
                  roundedCircle
                  style={{ width: "40px", height: "40px", marginRight: "10px" }}
                />
                <div>
                  <Card.Title className="mb-0">{post.name}</Card.Title>
                  <Card.Subtitle className="text-muted">
                    Được đăng bởi {post.postedBy.username} vào{" "}
                    {new Date(post.date).toLocaleDateString()}
                  </Card.Subtitle>
                </div>
              </Card.Header>
              <Card.Body>
                <Card.Text>{post.content}</Card.Text>
                {post.img && <Card.Img variant="top" src={post.img} />}
              </Card.Body>
              <Card.Footer className="bg-white">
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <Button
                    variant="link"
                    className="text-dark"
                    onClick={() => handleLike(post.id)}
                  >
                    <FaThumbsUp /> {post.likeCount} Thích
                  </Button>
                  <Button
                    variant="link"
                    className="text-dark ml-3"
                    onClick={() => handleShowModal(post.id)}
                  >
                    <FaComment /> Bình luận
                  </Button>
                  <Button variant="link" className="text-dark ml-3">
                    <FaShare /> Chia sẻ
                  </Button>
                </div>
              </Card.Footer>
              {comments[post.id] && (
                <div className="px-4">
                  {comments[post.id].map((comment) => (
                    <div key={comment.id} className="mb-2">
                      <strong>{comment.postedBy.username}: </strong>
                      {comment.content}
                    </div>
                  ))}
                </div>
              )}
            </Card>
          </Col>
        ))}
      </Row>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Thêm bài đăng mới</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={handleAddPost}>
            <div className="form-group">
              <label htmlFor="postName">Tên bài đăng</label>
              <input
                type="text"
                className="form-control"
                id="postName"
                placeholder="Nhập tên bài đăng"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="postContent">Nội dung</label>
              <textarea
                className="form-control"
                id="postContent"
                rows="3"
                placeholder="Nhập nội dung bài đăng"
                value={content}
                onChange={(e) => setContent(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="postImg">URL hình ảnh (tùy chọn)</label>
              <input
                type="text"
                className="form-control"
                id="postImg"
                placeholder="Nhập URL hình ảnh"
                value={img}
                onChange={(e) => setImg(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="postTags">Tags (phân cách bởi dấu phẩy)</label>
              <input
                type="text"
                className="form-control"
                id="postTags"
                placeholder="Nhập các tags"
                value={tags}
                onChange={(e) => setTags(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="postPostedBy">Người đăng (ID)</label>
              <input
                type="text"
                className="form-control"
                id="postPostedBy"
                placeholder="Nhập ID người đăng"
                value={postedBy}
                onChange={(e) => setPostedBy(e.target.value)}
                required
              />
            </div>
            <Button variant="primary" type="submit">
              Đăng
            </Button>
          </form>
        </Modal.Body>
      </Modal>

      <Modal show={showModal && modalPostId !== null} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Bình luận bài đăng</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={(e) => handleCommentSubmit(e, modalPostId)}>
            <div className="form-group">
              <textarea
                className="form-control"
                rows="3"
                placeholder="Nhập nội dung bình luận"
                value={commentContent}
                onChange={(e) => setCommentContent(e.target.value)}
                required
              />
            </div>
            <Button variant="primary" type="submit">
              Bình luận
            </Button>
          </form>
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default UserPage;
